template.declaration:
// Place local declarations here.
rid_t queue[length];
did_t origin_queue[length];


rid_t pop(){
    rid_t result = queue[0];
    int i;
    for(i = 0; i < length; ++i){
	    if(i != length - 1){
            queue[i] = queue[i + 1];
        }
	    else {
            queue[i] = -1;
        }
    }
    return result;
}

void add(rid_t recipe){
    int i;
    bool flag = true;
    for(i = 0; i < length && flag; ++i){
        if(queue[i] == -1){
            queue[i] = recipe;
            flag = false;
        }
    }
}

did_t origin_pop(){
    did_t result = origin_queue[0];
    int i;
    for(i = 0; i < length; ++i){
	    if(i != length - 1){
            origin_queue[i] = origin_queue[i + 1];
        }
	    else {
            origin_queue[i] = -1;
        }
    }
    return result;
}

void origin_add(did_t direction){
    int i;
    bool flag = true;
    for(i = 0; i < length && flag; ++i){
        if(origin_queue[i] == -1){
            origin_queue[i] = direction;
            flag = false;
        }
    }
}


bool not_full(){
    if(queue[length - 1] == -1)
        return true;
    else
        return false;
}

bool not_empty(){
    if(queue[0] != -1)
        return true;
    else
        return false;
}

void init_queues(){
    int i;
    for(i = 0; i < length; ++i){
        queue[i] = -1;
        origin_queue[i] = -1;
    }
}

int calculate_remaining(rid_t rid){
    int remaining = 0;
    int i;
    for(i = 0; i < NUMBER_OF_WORKTYPES; ++i)
        if(worktype[i])
            remaining += p_time[i] * cost_rate[i];
    return remaining;
}

clock local_c;
did_t origin;
did_t succ;

rid_t lock = -1;

bool is_callable(mid_t cid){
   int i;
   for(i = 0; i < NUMBER_OF_OUTPUTS; ++i){
        if(next[i] == cid){
            return true;
        }
    }
   return false;
}


did_safe_t inverse(did_safe_t direction){
//0 = up, 1 = right, 2 = down, 3 = left
if(direction == 0)
    return 2;
else if(direction == 1)
    return 3;
else if(direction == 2)
    return 0;
else
    return 1;

}

